import React, { useEffect, useState } from 'react';
import { getLibros, searchLibros, filterByGenre, filterByAuthor, filterByStatus, getGenres, getAuthors } from '../services/api';
import BookForm from './bookform';
import BookItem from './bookitem';

export default function BookList(){
  const [books, setBooks] = useState([]);
  const [editing, setEditing] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('id');
  const [sortOrder, setSortOrder] = useState('ASC');
  const [filterBy, setFilterBy] = useState('');
  const [filterValue, setFilterValue] = useState('');
  const [genres, setGenres] = useState([]);
  const [authors, setAuthors] = useState([]);

  const load = async () => {
    try {
      const res = await getLibros(sortBy, sortOrder, filterBy || null, filterValue || null);
      setBooks(res.data);
    } catch (err) { console.error(err); }
  };

  const loadGenresAndAuthors = async () => {
    try {
      const [genresRes, authorsRes] = await Promise.all([getGenres(), getAuthors()]);
      setGenres(genresRes.data);
      setAuthors(authorsRes.data);
    } catch (err) { console.error(err); }
  };

  const search = async (term) => {
    try {
      if (term.trim() === '') {
        load();
        return;
      }
      const res = await searchLibros(term);
      setBooks(res.data);
    } catch (err) { console.error(err); }
  };

  const handleFilter = async (filterType, value) => {
    try {
      let res;
      switch(filterType) {
        case 'genero':
          res = await filterByGenre(value);
          break;
        case 'autor':
          res = await filterByAuthor(value);
          break;
        case 'estado':
          res = await filterByStatus(value);
          break;
        default:
          return;
      }
      setBooks(res.data);
    } catch (err) { console.error(err); }
  };

  useEffect(()=>{ 
    load(); 
    loadGenresAndAuthors();
  }, [sortBy, sortOrder, filterBy, filterValue]);

  const created = b => setBooks(prev => [b, ...prev]);
  const updated = b => setBooks(prev => prev.map(x => x.id === b.id ? b : x));
  const deleted = id => setBooks(prev => prev.filter(x => x.id !== id));

  const handleSearch = (e) => {
    e.preventDefault();
    search(searchTerm);
  };

  const resetFilters = () => {
    setSearchTerm('');
    setFilterBy('');
    setFilterValue('');
    setSortBy('id');
    setSortOrder('ASC');
    load();
  };

  return (
    <div style={{ padding: 20 }}>
      <BookForm editing={editing} onCreated={created} onUpdated={updated} onCancel={() => setEditing(null)} />

      {/* Controles de ordenamiento y filtrado */}
      <div style={{ marginBottom: 20, padding: 15, backgroundColor: '#f8f9fa', borderRadius: 8, border: '1px solid #dee2e6' }}>
        <h3 style={{ margin: '0 0 15px 0', color: '#495057' }}>üîß Controles de Ordenamiento y Filtrado</h3>

        {/* Ordenamiento */}
        <div style={{ display: 'flex', gap: 10, alignItems: 'center', marginBottom: 15, flexWrap: 'wrap' }}>
          <label style={{ fontWeight: 'bold', color: '#495057' }}>Ordenar por:</label>
          <select 
            value={sortBy} 
            onChange={(e) => setSortBy(e.target.value)}
            style={{ padding: 6, border: '1px solid #ccc', borderRadius: 4 }}
          >
            <option value="id">ID</option>
            <option value="titulo">T√≠tulo</option>
            <option value="autor">Autor</option>
            <option value="genero">G√©nero</option>
            <option value="anio">A√±o</option>
            <option value="estado">Estado</option>
            <option value="fecha_registro">Fecha de Registro</option>
          </select>

          <select 
            value={sortOrder} 
            onChange={(e) => setSortOrder(e.target.value)}
            style={{ padding: 6, border: '1px solid #ccc', borderRadius: 4 }}
          >
            <option value="ASC">Ascendente (A-Z, 1-9)</option>
            <option value="DESC">Descendente (Z-A, 9-1)</option>
          </select>
        </div>

        {/* Filtros */}
        <div style={{ display: 'flex', gap: 10, alignItems: 'center', marginBottom: 15, flexWrap: 'wrap' }}>
          <label style={{ fontWeight: 'bold', color: '#495057' }}>Filtrar por:</label>
          <select 
            value={filterBy} 
            onChange={(e) => { setFilterBy(e.target.value); setFilterValue(''); }}
            style={{ padding: 6, border: '1px solid #ccc', borderRadius: 4 }}
          >
            <option value="">Sin filtro</option>
            <option value="genero">G√©nero</option>
            <option value="autor">Autor</option>
            <option value="estado">Estado</option>
          </select>

          {filterBy === 'genero' && (
            <select 
              value={filterValue} 
              onChange={(e) => setFilterValue(e.target.value)}
              style={{ padding: 6, border: '1px solid #ccc', borderRadius: 4 }}
            >
              <option value="">Seleccionar g√©nero</option>
              {genres.map(genre => (
                <option key={genre} value={genre}>{genre}</option>
              ))}
            </select>
          )}

          {filterBy === 'autor' && (
            <select 
              value={filterValue} 
              onChange={(e) => setFilterValue(e.target.value)}
              style={{ padding: 6, border: '1px solid #ccc', borderRadius: 4 }}
            >
              <option value="">Seleccionar autor</option>
              {authors.map(author => (
                <option key={author} value={author}>{author}</option>
              ))}
            </select>
          )}

          {filterBy === 'estado' && (
            <select 
              value={filterValue} 
              onChange={(e) => setFilterValue(e.target.value)}
              style={{ padding: 6, border: '1px solid #ccc', borderRadius: 4 }}
            >
              <option value="">Seleccionar estado</option>
              <option value="Disponible">Disponible</option>
              <option value="Prestado">Prestado</option>
              <option value="En reparaci√≥n">En reparaci√≥n</option>
            </select>
          )}
        </div>

        {/* Botones de acci√≥n */}
        <div style={{ display: 'flex', gap: 10, flexWrap: 'wrap' }}>
          <button 
            onClick={resetFilters}
            style={{ padding: 8, backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: 4, cursor: 'pointer' }}
          >
            üîÑ Limpiar Filtros
          </button>
        </div>
      </div>

      {/* Barra de b√∫squeda */}
      <div style={{ marginBottom: 20, display: 'flex', gap: 10, alignItems: 'center' }}>
        <form onSubmit={handleSearch} style={{ display: 'flex', gap: 10, alignItems: 'center' }}>
          <input
            type="text"
            placeholder="Buscar por t√≠tulo..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ padding: 8, border: '1px solid #ccc', borderRadius: 4, minWidth: 200 }}
          />
          <button type="submit" style={{ padding: 8, backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: 4, cursor: 'pointer' }}>
            üîç Buscar
          </button>
        </form>
        <button 
          onClick={() => { setSearchTerm(''); load(); }} 
          style={{ padding: 8, backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: 4, cursor: 'pointer' }}
        >
          üìã Ver Todos
        </button>
      </div>

      {/* Informaci√≥n de resultados */}
      <div style={{ marginBottom: 10, color: '#6c757d', fontSize: '14px' }}>
        Mostrando {books.length} libro{books.length !== 1 ? 's' : ''}
        {sortBy !== 'id' || sortOrder !== 'ASC' ? ` ‚Ä¢ Ordenado por ${sortBy} (${sortOrder})` : ''}
        {filterBy && filterValue ? ` ‚Ä¢ Filtrado por ${filterBy}: ${filterValue}` : ''}
      </div>

      <table>
        <thead><tr><th>ID</th><th>T√≠tulo</th><th>Autor</th><th>A√±o</th><th>Estado</th><th>Acciones</th></tr></thead>
        <tbody>{books.map(b => <BookItem key={b.id} book={b} onEdit={(bk)=>setEditing(bk)} onDeleted={deleted} />)}</tbody>
      </table>
    </div>
  );
}
